let interval,currentIndex=0;function moveCarrossel(a){const b=document.querySelectorAll(".carrossel-item"),c=b.length;b[currentIndex].classList.remove("active"),currentIndex=a===void 0?(currentIndex+1)%c:a,b[currentIndex].classList.add("active");const d=document.querySelector(".carrossel-inner");d.style.transform=`translateX(-${100*currentIndex}%)`,document.querySelectorAll(".carrossel-controls input")[currentIndex].checked=!0}function startCarrossel(){interval=setInterval(()=>moveCarrossel(),3e3)}document.addEventListener("DOMContentLoaded",()=>{document.querySelector(".carrossel-item").classList.add("active"),startCarrossel(),document.querySelectorAll(".carrossel-controls input").forEach((a,b)=>{a.addEventListener("change",()=>{clearInterval(interval),moveCarrossel(b),startCarrossel()})})});